import React from "react";
import { motion } from "framer-motion";
import { FaLaptopCode, FaBriefcase, FaGraduationCap } from "react-icons/fa";
import Button from "../components/Button";
import Card from "../components/Card";

const services = [
  { icon: <FaBriefcase size={50} />, title: "Business Websites", description: "Professional and responsive websites for businesses of all sizes." },
  { icon: <FaGraduationCap size={50} />, title: "Educational Websites", description: "Engaging e-learning platforms and school websites." },
  { icon: <FaLaptopCode size={50} />, title: "Custom Software", description: "Tailor-made software solutions for your unique needs." },
];

export default function HomePage() {
  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-6">
      <motion.h1 initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 1 }} className="text-4xl font-bold text-blue-600 mb-4">
        Welcome to Lucky Software Solutions
      </motion.h1>
      <motion.p initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 1.2 }} className="text-lg text-gray-700 text-center max-w-2xl mb-6">
        We specialize in developing all kinds of software solutions, including business and educational websites. Let’s bring your ideas to life!
      </motion.p>
      <div className="grid md:grid-cols-3 gap-6">
        {services.map((service, index) => (
          <Card key={index}>
            <div className="flex justify-center mb-4 text-blue-500">{service.icon}</div>
            <h2 className="text-xl font-semibold text-gray-800">{service.title}</h2>
            <p className="text-gray-600 mt-2">{service.description}</p>
          </Card>
        ))}
      </div>
      <motion.div whileHover={{ scale: 1.1 }} className="mt-8">
        <Button>Contact Us</Button>
      </motion.div>
    </div>
  );
}
