//import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { motion } from "framer-motion";
import { FaLaptopCode, FaBusinessTime, FaGraduationCap } from "react-icons/fa";

export default function HomePage() {
  return (
    <div className="min-h-screen bg-gray-100 text-gray-900">
      <header className="bg-blue-600 text-white py-4 text-center text-2xl font-bold">
        Lucky Software Solutions
      </header>

      <motion.div 
        initial={{ opacity: 0, y: -20 }} 
        animate={{ opacity: 1, y: 0 }} 
        transition={{ duration: 0.5 }}
        className="text-center my-8"
      >
        <h2 className="text-3xl font-semibold">We Build Your Digital Presence</h2>
        <p className="text-lg mt-2 text-gray-700">
          Websites | Web Applications | Custom Software Solutions
        </p>
      </motion.div>

      <div className="grid md:grid-cols-3 gap-6 px-8">
        <ServiceCard 
          icon={<FaBusinessTime size={40} />} 
          title="Business Websites" 
          description="Professional and modern websites for businesses to grow online." 
        />
        <ServiceCard 
          icon={<FaGraduationCap size={40} />} 
          title="Educational Websites" 
          description="E-learning platforms, school websites, and online education solutions." 
        />
        <ServiceCard 
          icon={<FaLaptopCode size={40} />} 
          title="Custom Software" 
          description="Tailor-made software solutions to meet your business needs." 
        />
      </div>

      <div className="text-center my-10">
        <Button className="bg-blue-600 text-white px-6 py-3 text-lg rounded-lg shadow-md hover:bg-blue-700">
          Get in Touch
        </Button>
      </div>

      <footer className="bg-gray-800 text-white text-center py-4">
        &copy; {new Date().getFullYear()} Lucky Software Solutions. All rights reserved.
      </footer>
    </div>
  );
}

function ServiceCard({ icon, title, description }) {
  return (
    <Card className="shadow-lg rounded-lg p-6 bg-white text-center">
      <div className="flex justify-center text-blue-600 mb-4">{icon}</div>
      <h3 className="text-xl font-bold">{title}</h3>
      <p className="text-gray-700 mt-2">{description}</p>
    </Card>
  );
}
